import { useQuestion } from "../../src/services/question/use-question.js";
import { theCresimsLogo, theCresimsLogoby } from "./theCresimsLogo.js";
import {setPersonagem, getPersonagem, getTodosPersonagens, deletarPersonagem  } from "../../src/services/crud/personagem.js";
import { clearBash } from "../../src/requisitos/comuns.js";
import { getStorage, getStorageDead  } from "../../src/services/crud/storage.js"
 import { mortePersonagem } from "./mortePersonagem.js"

export const startMenu = async () => {
  let mensagemAtencao = ``;
  let startMenu = true;
  const menucriarPersonagem = "\x1b[36m⭐ Criar Personagem\x1b[0m";
  const menuescolherPersonagem = "\x1b[32m👤 Escolher Personagem\x1b[0m";
  const menulistarPersonagens = "\x1b[33m📋 Listar Personagens\x1b[0m";
  const menudeletarPersonagem = "\x1b[34m🗑️  Deletar Personagem\x1b[0m";
  const menuvisitarCemiterio = "\x1b[35m🌟 Visitar Cemitério dos Personagens\x1b[0m";
  const menufinalizarJogo = "\x1b[31m❌ Finalizar jogo\x1b[0m";
  while (startMenu == true) {

    const input = await useQuestion(`
    ░▒█▀▀▄░▒█▀▀▀░▒█▀▄▀█░░░▒█░░▒█░▀█▀░▒█▄░▒█░▒█▀▀▄░▒█▀▀▀█
    ░▒█▀▀▄░▒█▀▀▀░▒█▒█▒█░░░░▒█▒█░░▒█░░▒█▒█▒█░▒█░▒█░▒█░░▒█
    ░▒█▄▄█░▒█▄▄▄░▒█░░▒█░░░░░▀▄▀░░▄█▄░▒█░░▀█░▒█▄▄█░▒█▄▄▄█ 
${await theCresimsLogo()}
${await theCresimsLogoby()}

Escolha uma das opções:
${mensagemAtencao}

1. ${menucriarPersonagem}

2. ${menuescolherPersonagem}

3. ${menulistarPersonagens}

4. ${menudeletarPersonagem}

5. ${menuvisitarCemiterio}

X. ${menufinalizarJogo}

Sua escolha: `);

    switch (input.toUpperCase()) {
      case "1":
        return setPersonagem();
      case "2":
        return getPersonagem();
      case "3":
        await getTodosPersonagens()
        break;
      case "4":
        const id = await menuDelete();
        if (id) {
          deletarPersonagem(id);
        }
        break;
      case "5":
        const personagemMorto = await menuCemiterio();
        if (personagemMorto) {
          await mortePersonagem(personagemMorto); 
        }
        break;
      case "X":
        console.log("\nFoi Ótimo ter você aqui!! \nAte a proxima!!");
        return "exit";
      default:
        clearBash();
        mensagemAtencao = `
### Escolha uma opção válida ###
`;
    }
  }
};




const menuCemiterio = async () => {
  const storage = getStorageDead();
  clearBash();
  console.log(`
### Escolha um personagem para Visitar tumulo ###
`);

  for (let cont = 0; cont < storage.length; cont++) {
    console.log(`${cont + 1} - ${storage[cont].nome}`);
  }
  console.log(`X - Retornar ao menu principal.`);

  const id = await useQuestion(`Escolha o id do personagem: `);

  if (id != "X" || id != "x") {
    return storage[id - 1];
  }
};

const menuDelete = async () => {
  const storage = getStorage();

  console.log(`
### Delete um personagem ###
`);

  for (const obj of storage) {
    console.log(`${obj.id} - ${obj.nome} (Tempo restante: ${obj.tempo})`);
  }
  console.log(`X - Retornar ao menu principal.`);

  const id = await useQuestion(`Escolha o id do personagem: `);

  if (id != "X" || id != "x") {
    return id;
  }
};

